import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate} from 'react-router-dom';

function AddFunds() {
  const nav = useNavigate();
  const [formData, setFormData] = useState({
    amount: '',
    method: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/add_funds', formData)
    .then(response => {
      nav('/dashboard');
    })
    .catch(error => {
      console.error("There was an error adding the funds!", error);
    });
  };

  return (
    <div>
      <h1>Add Funds</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Amount:
          <input type="number" name="amount" value={formatData.amount} onChange={handleChange} required />
        </label>
        <label>
          Method:
          <input type="text" name="method" value={formatData.method} onChange={handleChange} required />
        </label>
        <button type="submit">Add Funds</button>
      </form>
    </div>
  );
}

export default AddFunds;
