import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function AddExpense() {
  const history = useHistory();
  const [formData, setFormData] = useState({
    date: '',
    descr: '',
    amount: '',
    method: '',
    category: '',
    type: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/add_expense', formData)
    .then(response => {
      history.push('/dashboard');
    })
    .catch(error => {
      console.error("There was an error adding the expense!", error);
    });
  };

  return (
    <div>
      <h1>Add Expense</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Date:
          <input type="date" name="date" value={formData.date} onChange={handleChange} required />
        </label>
        <label>
          Description:
          <input type="text" name="descr" value={formData.descr} onChange={handleChange} required />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={formData.amount} onChange={handleChange} required />
        </label>
        <label>
          Method:
          <input type="text" name="method" value={formData.method} onChange={handleChange} required />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={formData.category} onChange={handleChange} required />
        </label>
        <label>
          Type:
          <input type="text" name="type" value={formData.type} onChange={handleChange} required />
        </label>
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
}

export default AddExpense;
